// src/components/RFIDCounter.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const RFIDCounter = () => {
  const [tagCount, setTagCount] = useState(0);
  const [previousTags, setPreviousTags] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('/rfid');
        const currentTags = response.data;
        
        // นับจำนวน ID tags ที่ไม่เคยนับมาก่อน
        const newTags = currentTags.filter(tag => !previousTags.includes(tag));
        setTagCount(tagCount + newTags.length);

        // เซ็ต ID tags เพื่อใช้เป็นตัวเปรียบเทียบในการนับในครั้งถัดไป
        setPreviousTags(currentTags);
      } catch (error) {
        console.error('Error fetching RFID tags:', error);
      }
    };

    const intervalId = setInterval(fetchData, 1000);

    return () => {
      clearInterval(intervalId);
    };
  }, [tagCount, previousTags]);

  return (
    <div>
      <h1>RFID Tag Count: {tagCount}</h1>
    </div>
  );
};

export default RFIDCounter;
