// src/components/RFIDCounter.js
import React, { useState, useEffect } from 'react';

const RFIDCounter = () => {
  const [rfidTags, setRfidTags] = useState([]);

  useEffect(() => {
    const eventSource = new EventSource('/sse');

    eventSource.onmessage = (event) => {
      const newRfidTag = JSON.parse(event.data);
      setRfidTags((prevTags) => [...prevTags, newRfidTag]);
    };

    return () => {
      eventSource.close();
    };
  }, []);

  return (
    <div>
      <h1>RFID Tags</h1>
      <ul>
        {rfidTags.map((tag, index) => (
          <li key={index}>{tag}</li>
        ))}
      </ul>
    </div>
  );
};

export default RFIDCounter;
